package estructuras_control;
public class Ejercicio2_14 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		// EJERCICIO 14
		/*------------------------------------------------
		 * Visualizar la tabla del 1.
		 *------------------------------------------------*/
		
		int num=1, resultado;
		
		do {
			
			resultado=(1*num);
			System.out.println("1x"+num+"="+resultado);
			num++;
		}
		while (num<=10);

	}

}
