package ejercicios;

public class Prueba2 {	

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		System.out.println("El valor absoluto es: "+ Math.abs(-8));
		System.out.println("La raíz cuadrada de 4 es: "+ Math.sqrt(4));
		
		double n=253354.7d;
		
		System.out.println("La raiz "+Math.sqrt(n));
		System.out.println("7 elevado a 2 es "+Math.pow(7, 2));
		System.out.println("El valor de PI es: " +Math.PI);
		System.out.println("Números aleatorios " +Math.random());
		System.out.println(Math.random()*2);
		System.out.println((int) (Math.random()*2));
		System.out.println((int) Math.random()*2);

	}

}
